INSERT INTO `email_queue` (`id`, `destinataire`, `nom_destinataire`, `sujet`, `message`, `statut`, `tentatives`, `date_creation`, `date_envoi`, `erreur`) VALUES
(1, 'webgeniuses12@gmail.com', 'Nick jomas effe', 'Nouvelle offre d\'emploi correspondant à vos critères', '\r\n                    <!DOCTYPE html>\r\n                    <html>\r\n                    <head>\r\n                        <meta charset=\'utf-8\'>\r\n                        <meta name=\'viewport\' content=\'width=device-width, initial-scale=1.0\'>\r\n                        <title>Nouvelle offre d\'emploi</title>\r\n                        <style>\r\n                            * {\r\n                                margin: 0;\r\n                                padding: 0;\r\n                                box-sizing: border-box;\r\n                                font-family: \'Segoe UI\', Tahoma, Geneva, Verdana, sans-serif;\r\n                            }\r\n                            body {\r\n                                background-color: #f5f5f5;\r\n                                color: #333333;\r\n                                line-height: 1.6;\r\n                            }\r\n                            .email-container {\r\n                                max-width: 600px;\r\n                                margin: 0 auto;\r\n                                background-color: #ffffff;\r\n                                border-radius: 8px;\r\n                                overflow: hidden;\r\n                                box-shadow: 0 4px 8px rgba(0, 0, 0, 0.05);\r\n                            }\r\n                            .email-header {\r\n                                background-color: #0671dc;\r\n                                padding: 30px 20px;\r\n                                text-align: center;\r\n                            }\r\n                            .email-header img {\r\n                                max-width: 180px;\r\n                                height: auto;\r\n                            }\r\n                            .email-body {\r\n                                padding: 40px 30px;\r\n    